package com.example.taskPalette.utilsimport java.time.format.DateTimeFormatterobject DateUtils {    var formatter: DateTimeFormatter = DateTimeFormatter.ofPattern("yyyy-MM-dd HH:mm:ss")    fun getRemainingTime(remainingTimeInSeconds: Long): String {        val mDays = secondsToDays(remainingTimeInSeconds)        val mHour = secondsToHours(remainingTimeInSeconds) - mDays.toInt() * 24        val mMin = secondsToMinutes(remainingTimeInSeconds) - mHour.toInt() * 60 - mDays.toInt() * 60 * 24        val mSec = remainingTimeInSeconds - (secondsToMinutes(remainingTimeInSeconds).toInt() * 60)        val dayNum = mDays.toInt()        val hourNum = mHour.toInt()        val minNum = mMin.toInt()        val secNum = mSec.toInt()        val strTime = StringBuilder().apply {            if (dayNum != 0) {                append(dayNum).append("D").append(" : ")            }            if (hourNum != 0) {                append(hourNum).append("H").append(" : ")            }            if (minNum != 0) {                append(minNum).append("m").append(" : ")            }            append(secNum).append("sec")        }        return strTime.toString()    }    private fun secondsToDays(timeInSeconds: Long): Double {        return secondsToHours(timeInSeconds) / 24.0    }    private fun secondsToHours(timeInSeconds: Long): Double {        return timeInSeconds / 3600.0    }    private fun secondsToMinutes(timInSeconds: Long): Double {        return timInSeconds / 60.0    }}